mapper.select:
SELECT apply.*, review1.*, review2.*, user.*
FROM review2, review1, apply, user
WHERE apply.item_id = review2.item_id
AND review1.item_id = review2.item_id
AND user.real_name = apply.item_user

	AND review2_user = #{review2_user}


	AND review2_status = #{review2_status}

LIMIT #{start},#{pageSize}

SELECT COUNT(*) FROM review2, review1, apply, user WHERE apply.item_id = review2.item_id AND review1.item_id = review2.item_id AND user.real_name = apply.item_user
AND review2_user = #{review2_user}
AND review2_status = #{review2_status}
