mapper.select:
SELECT * FROM apply


		AND item_submit = #{item_submit}


		AND item_status = #{item_status}


		AND (item_name LIKE '%${str}%' OR item_user LIKE '%${str}%')



			AND user_department = #{apply.user_department}


			AND item_type = #{apply.item_type}



ORDER BY apply_time ASC
LIMIT #{start},#{pageSize}

SELECT count(*) FROM apply


		AND item_submit = #{item_submit}


		AND item_status = #{item_status}


		AND (item_name LIKE '%${str}%' OR item_user LIKE '%${str}%')



			AND user_department = #{apply.user_department}


			AND item_type = #{apply.item_type}

mapper.delete:
DELETE FROM apply
WHERE item_id IN

	#{item_id}

mapper.update:
UPDATE apply
SET item_status = #{item_status}
WHERE item_id = #{item_id}
