mapper.select:
SELECT * FROM user


		AND user_type = #{user_type}


LIMIT #{start},#{pageSize}

mapper.update:
UPDATE user
SET user_pass = #{user_pass}
WHERE user_name = #{user_name}

UPDATE signln
SET user_pass = #{user_pass},
	user_type = #{user_type},
	signln_valid = #{signln_valid}
WHERE user_name = #{user_name}

UPDATE signln
SET user_pass = #{user_pass}
WHERE user_name = #{user_name}

mapper.delete:
DELETE FROM user
WHERE user_id IN 

	#{user_id}

DELETE FROM signln
WHERE user_name IN 

	#{user_name}
